import { Chess } from 'chess.js';
import React from 'react';


export const onDrop = (
    game: Chess,
    setFen: (fen: string) => void,
    setMoves: React.Dispatch<React.SetStateAction<string[]>>,
    sourceSquare: string,
    targetSquare: string,
): boolean => {
    try {
        const move = game.move({
            from: sourceSquare,
            to: targetSquare,
            promotion: 'q',
        });

        if (move) {
            const moveString: string = move.from + move.to + (move.promotion ? move.promotion : '');

            setFen(game.fen());
            setMoves((prev: string[]) => [...prev, moveString]);

            return true;
        }

        return false;
    } catch(e) {
        console.log(e);
        return false;
    }
};

export function isGameOver(game: Chess): boolean {


    return false;
}
