import { SavedGameContent } from '../models/models';


export function retrieveEpisodesNumFromModelFileName(filename: string | undefined, isView: boolean): string {
    if (filename && !isView) {
        const match: RegExpMatchArray | null | undefined = filename?.match(/episode(\d+)/);
        if (match) {
            return match[1];
        }
    }

    return '-1';
}

export function generateGroupedMoves(savedGameContent: SavedGameContent | undefined): string[][] {
    if (savedGameContent) {
        const moves: string[] = savedGameContent.moves;
        const groupedMoves: string[][] = [];

        for (let i = 0; i < moves.length; i += 2) {
            groupedMoves.push(moves.slice(i, i + 2));
        }

        return groupedMoves;
    }

    return [];
}
